#include <xc.h>
#include <stdio.h>
#include "mcc_generated_files/mcc.h"
int reading_pushbutton=0;
void main(void)
{
    SYSTEM_Initialize();
    IO_RD2_SetDigitalOutput(); 
    IO_RA0_SetDigitalInput(); 
    IO_RA0_SetPullup(); 
    IO_RD2_SetLow(); 
    while (1) 
    {
        reading_pushbutton=IO_RA0_GetValue(); 
        __delay_ms(100);
        reading_pushbutton=IO_RA0_GetValue();
        if (reading_pushbutton==LOW){
            IO_RD2_Toggle(); 
        }
    }
}
