#include "mcc_generated_files/mcc.h"
static uint16_t LM35read=0;
float temp=0.0;
void main(void)
{
    // initialize the device
    SYSTEM_Initialize();
    ADC_Initialize();
    while (1)
    {
        IO_RD2_SetLow();
        IO_RD3_SetLow();
        LM35read=ADC_GetConversion(channel_ANA0);
        temp=(5.0/1023)*LM35read;
        if (temp>=23.0 && temp<=26.0)
        {
            IO_RD3_SetHigh();
        } else {
          IO_RD2_SetHigh();
        }
        __delay_ms(500);
    }
}
