float EulerIntegration(float pos, float vel, float accel, 
   float totalTime, float dt) 
{ 
  float time = 0.0f; 
  while (time < totalTime) 
  { 
    pos += vel * dt; 
    vel += accel * dt; 
    time += dt; 
    } 
  return pos; 
}

