   {
      virtual ~control_concept() = default;
      virtual void draw() = 0;
   };
   template <typename T>
   struct control_model : public control_concept
   {
      control_model(T& unit) : t(unit) {}
      void draw() override
 {
         t.erase_background();
         t.paint();
      }
   private:
      T& t;
   };
private:
   std::vector<std::shared_ptr<control_concept>> ctrls;
}; 
